package com.syntax.class09;

public class MoreNestedLoopsExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// HOW TO CREAT MULTIPLICATION TABLE 
		for(int x=1; x<=10; x++) {
			for(int y=1; y<=10; y++) {
				System.out.println(x+" x " +y+" = "+(x*y));
			}
		}
	}

}
